	  exports.debuggeR.updateInfo();

	  $("#code").focus();
	  $("#runButton").prop("disabled", true);
	  $("#runButton").html("Run");
	  $("#compileButton").prop("disabled", false);
	  $("#fileSelect").prop("disabled", false);
	  $("#stepButton").prop("disabled", true);
	  $("#gotoButton").prop("disabled", true);
